def count_word_in_grid(grid: list[str], word: str) -> int:
    def search_from_position(x: int, y: int, dx: int, dy: int) -> int:
        """Search for the word starting from (x, y) in direction (dx, dy)."""
        for i in range(len(word)):
            nx, ny = x + i * dx, y + i * dy
            if nx < 0 or ny < 0 or nx >= len(grid) or ny >= len(grid[0]) or grid[nx][ny] != word[i]:
                return 0
        return 1

    directions = [
        (0, 1),  # right
        (1, 0),  # down
        (1, 1),  # down-right
        (1, -1), # down-left
        (0, -1), # left
        (-1, 0), # up
        (-1, -1),# up-left
        (-1, 1)  # up-right
    ]

    count = 0
    for x in range(len(grid)):
        for y in range(len(grid[0])):
            for dx, dy in directions:
                count += search_from_position(x, y, dx, dy)
    return count

@script_handler
def main() -> None:
    grid = input.strip().split('\n')
    word = "XMAS"
    count = count_word_in_grid(grid, word)
    print(f"The word '{word}' is found {count} times in the grid.")
    
if __name__ == "__main__":
    main()