def count_xmas_in_grid(grid: list[str]) -> int:
    def is_xmas(x, y):
        # Check if the center coordinate is valid
        if (x < 1 or x >= len(grid) - 1 or 
            y < 1 or y >= len(grid[0]) - 1):
            return 0
        
        # Define the 3x3 area to check
        area = [
            [grid[x-1][y-1], grid[x][y-1], grid[x+1][y-1]],
            [grid[x-1][y],   grid[x][y],   grid[x+1][y]],
            [grid[x-1][y+1], grid[x][y+1], grid[x+1][y+1]]
        ]
        isXmas = True
        
        # Check diagonal from top-left to bottom-right or vice versa
        if not (((area[0][0] == 'M' and 
            area[2][2] == 'S') or
            (area[0][0] == 'S' and
            area[2][2] == 'M')) and area[1][1] == 'A'):
                isXmas = False
        
        # Check diagonal from bottom-left to top-right or vice versa
        if not (((area[2][0] == 'M' and 
            area[0][2] == 'S') or
            (area[2][0] == 'S' and
            area[0][2] == 'M')) and area[1][1] == 'A'):
                isXmas = False
        
        if isXmas:
            return 1
        else:
            return 0

    count = 0
    for x in range(len(grid)):
        for y in range(len(grid[0])):
            isXmas = is_xmas(x, y)
            count += isXmas
    return count

@script_handler
def main() -> None:
    grid = input.strip().split('\n')
    count = count_xmas_in_grid(grid)
    print(f"The X-MAS pattern is found {count} times in the grid.")
    
if __name__ == "__main__":
    main()